import {useState, useEffect} from 'react';
import { Link } from 'react-router-dom';
export default function Home() {
    const [countries, setCountries] = useState([]);
    useEffect(() =>{
        const getCountries = async () => {
            await fetch("http://localhost:4000/get-countries")
            .then(async (data) => {
                const response = await data.json();
                setCountries(response);
            }
            );
        }

    }, []);

    return(<div className="grid grid-cols-4 gap-10 pt-8">
          {countries.map((country) => (
            <Link to={`/country/${country.id}`} key={country.id}>
              <div className="bg-gray-100 rounded-xl p-4 hover:shadow-md transition duration-300">
                <h3 className="text-xl font-semibold">{country.name}</h3>
                <p className="text-gray-500 mt-2">{country.description}</p>
                <img src={country.image} alt={country.name} className="w-full mt-4 rounded-xl" />
              </div>
            </Link>
          ))}
    </div>)

}
