import { useState, useEffect } from 'react';
import { useParams, useNavigate, Link } from 'react-router-dom';

export default function UpdateCountry() {
    const {id} = useParams();
    const [country, setCountry] = useState({});
    const navigate = useNavigate();

    const handleFormChange = (e) => {
        const {name, value} = e.target;
        setCountry({...country, [name]: value});
    }

    const updateCountry = async () => {
        await fetch(`http://localhost:4000/update-country/${id}`, {
            method: "PATCH",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(country)
        }).then(async (data) => {
            const response = await data.json();
            if (response.success) {
                navigate("/");
            }
        });
    }

    useEffect(() => {
        const getCountry = async () => {
            const response = await fetch(`http://localhost:4000/get-country/${id}`);
            if (!response.ok) {
                throw new Error('Network response was not ok');
            }
            // Only parse the response as JSON if there's content
            if (response.status !== 204) {
                const data = await response.json();
                setCountry(data);
            }
        }
        getCountry();
    }, [id]);

    return (
        <div className=" flex flex-col gap-4 pt-8">
            <h2 className="text-xl">Add new country</h2>
            <input name="name" value={country.name} onChange={handleFormChange} type="text" placeholder="Country name" className="border-2 border-gray-300 rounded-xl" />
            <input name="description" value={country.description} onChange={handleFormChange} type="text" placeholder="Description" className="border-2 border-gray-300 rounded-xl" />
            <input name="image" value={country.image} onChange={handleFormChange} type="text" placeholder="ImageUrl" className="border-2 border-gray-300 rounded-xl" />
            <button onClick={updateCountry} className="px-4 py-2 bg-blue-500 hover:bg-blue-700 text-white rounded-xl">Update Country</button>
        </div>
    )
}
