import { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
export default function Country() {
    const {id} = useParams();
    const [country, setCountry] = useState({});
    useEffect(()=> {
        const getCountry = async ()=>{
            await fetch(`http://localhost:4000/get-country/${id}`)
            .then(async(data) =>{
                const response = await data.json();
                setCountry(response);
            })
        }
        getCountry();
    }, []);
    return (
        <div className="flex flex-col pt-8 gap-6">
            <img src={country.image} className="w-full h-80 object-cover " alt='no pic added'/>
            <div className="flex justify-between items-center">
                <h1 className="text-xl font-semibold">{country.name}</h1>
                <div>
                    <button className="bg-red-500 py-2 px-4 text-white rounded-xl">Delete</button>
                </div>  
            </div>
         
            <p>{country.description}</p>
        </div>
    )
}
